import org.example.infrastructure.adapter.output.entity.UserEntity;
import org.example.infrastructure.adapter.output.repositories.JpaUserRepository;
import org.example.domain.model.User;
import org.example.application.port.output.UserRepositoryUseCase;
import org.springframework.stereotype.Component;

import java.util.Optional;

@Component
public class UserRepositoryUseCaseAdapter implements UserRepositoryUseCase {

 private final JpaUserRepository jpaUserRepository;

 public UserRepositoryUseCaseAdapter(JpaUserRepository jpaUserRepository) {
 this.jpaUserRepository = jpaUserRepository;
 }

 @Override
 public Optional<User> findById(String id) {
 return jpaUserRepository.findById(id).map(this::toDomain);
 }

 @Override
 public void save(User user) {
 UserEntity entity = toEntity(user);
 jpaUserRepository.save(entity);
 }

 @Override
 public Object findbyEmail(String mail) {
 return jpaUserRepository.findByEmail(mail);
 }

 @Override
 public User findByUserName(String username) {
 return jpaUserRepository.findByUserName(username);
 }

 private User toDomain(UserEntity entity) {
 return new User(
 entity.getId(),
 entity.getFirstName(),
 entity.getLastName(),
 entity.getUserName(),
 entity.getEmail()
 );
 }

 private UserEntity toEntity(User user) {
 UserEntity entity = new UserEntity();
 entity.setId(user.getId());
 entity.setFirstName(user.getFirstName());
 entity.setLastName(user.getLastName());
 entity.setEmail(user.getEmail());
 return entity;
 }

 public Optional<User> findDomainUserByUserName(String username) {
 return jpaUserRepository.findByUserName(username)
 .map(this::mapToDomain);
 }

 private User mapToDomain(UserEntity entity) {
 return new User(entity.getId(), entity.getFirstName(), entity.getLastName(),
 entity.getUserName(), entity.getEmail());
 }

}
