 package org.example.infrastructure.adapter.input.implemetation;

+import lombok.RequiredArgsConstructor;
+import org.example.application.port.output.UserRepositoryUseCase;
+import org.example.domain.model.User;
+import org.example.infrastructure.adapter.output.walrus.CloudServiceUseCase;
+import org.springframework.stereotype.Service;
+import org.springframework.web.multipart.MultipartFile;
+
+import java.util.Optional;
+
+@Service
+@RequiredArgsConstructor
 public class ProfilePictureService {
+ 
+ private final CloudServiceUseCase cloudService;
+ private final UserRepositoryUseCase userRepository;
+ 
+ public String uploadProfilePicture(String userId, MultipartFile file) {
+ // Validate user exists
+ User user = userRepository.findById(userId)
+ .orElseThrow(() -> new IllegalArgumentException("User not found with ID: " + userId));
+ 
+ // Validate file
+ if (file.isEmpty()) {
+ throw new IllegalArgumentException("File cannot be empty");
+ }
+ 
+ // Validate file type
+ String contentType = file.getContentType();
+ if (contentType == null || !contentType.startsWith("image/")) {
+ throw new IllegalArgumentException("File must be an image");
+ }
+ 
+ try {
+ // Upload to cloud storage
+ String blobId = cloudService.upload(file);
+ 
+ // Create file URL
+ String fileUrl = "https://walrus-storage.example.com/" + blobId;
+ 
+ // Update user profile
+ user.setProfilePictureUrl(fileUrl);
+ userRepository.save(user);
+ 
+ return fileUrl;
+ } catch (Exception e) {
+ throw new RuntimeException("Failed to upload profile picture: " + e.getMessage());
+ }
+ }
