```java
package org.example.domain.service;

import org.example.infrastructure.adapter.output.entity.ProfileDataEntity;
import org.springframework.stereotype.Service;

import java.math.BigDecimal;

@Service
public class CreditScoreCalculator {

 // Define constants for magic numbers
 private static final BigDecimal INFLOW_THRESHOLD_1 = new BigDecimal("50000");
 private static final BigDecimal INFLOW_THRESHOLD_2 = new BigDecimal("20000");
 private static final BigDecimal INFLOW_THRESHOLD_3 = new BigDecimal("10000");
 private static final BigDecimal INFLOW_THRESHOLD_4 = new BigDecimal("5000");
