 BigDecimal inflow = profileData.getMonthlyWalletInflow();
 if (inflow == null) {
 return 0;
 }

 if (inflow.compareTo(INFLOW_THRESHOLD_1) > 0) {
 return 20;
 }
 if (inflow.compareTo(INFLOW_THRESHOLD_2) > 0) {
 return 15;
 }
 if (inflow.compareTo(INFLOW_THRESHOLD_3) > 0) {
 return 10;
 }
 if (inflow.compareTo(INFLOW_THRESHOLD_4) > 0) {
 return 5;
 }
 return 0;
 }

