import org.example.infrastructure.adapter.input.dto.responses.SignUpResponseDto;
import org.example.infrastructure.adapter.input.dto.responses.UploadProfilePicResponseDto;
import org.example.infrastructure.adapter.input.dto.responses.UploadProfileResponseDto;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;
import org.springframework.web.multipart.MultipartFile;

@RestController
@RequestMapping("/api/v1/users")
public class UserController {

 private final AuthUseCase authUseCase;
 private final UpdateUserProfileUseCase updateUserProfileUseCase;

 public UserController(AuthUseCase authUseCase, UpdateUserProfileUseCase updateUserProfileUseCase) {
 this.authUseCase = authUseCase;
 this.updateUserProfileUseCase = updateUserProfileUseCase;
 }

 @PostMapping("/signup")
 public ResponseEntity<SignUpResponseDto> signUp(@RequestBody SignUpUserDto signUpUserDto) {
 SignUpResponseDto response = authUseCase.signUp(signUpUserDto);
 return new ResponseEntity<>(response, HttpStatus.CREATED);
 }

 @PostMapping("/login")
 public ResponseEntity<LoginResponse> login(@RequestBody LoginRequest loginRequest) {
 LoginResponse response = authUseCase.login(loginRequest);
 return ResponseEntity.ok(response);
 }

 @PostMapping("/{userId}/profile-picture")
 public ResponseEntity<UploadProfilePicResponseDto> uploadProfilePicture(
 @PathVariable String userId,
 @RequestParam("file") MultipartFile file
 ) {
 UploadProfilePicResponseDto response = updateUserProfileUseCase.uploadProfilePicture(userId, file);
 return ResponseEntity.ok(response);
 }

 @PostMapping("/profile-data")
 public ResponseEntity<UploadProfileResponseDto> submitProfileData(@RequestBody UploadProfileRequestDto profileRequest) {
 UploadProfileResponseDto response = updateUserProfileUseCase.submitProfileData(profileRequest);
 return ResponseEntity.ok(response);
 }
}
